var request = require('request');
var cheerio = require('cheerio');

module.exports = {
	get: function(text, callback){
	request("http://www.asciiarts.net/figlet.ajax.php?message="+text+"&font=standard.flf", function(err, request, body){
		var $ = cheerio.load(body);
		callback($("pre").text());
	});
	}

}
